import io from 'socket.io-client';

const { useState } = require("react");

function App() {
  const [messages, setMessages] = useState([]);
  const socket = io('localhost:3000');

  socket.on('SERVER_MSG', msg => {
    setNewMessage(msg);
  });

  function setNewMessage(msg) {
    setMessages([
      ...messages,
      msg
    ]);
  }
  function sendMessage(e) {
    e.preventDefault();
    const msg = {
      username: e.target.username.value,
      text: e.target.text.value
    };
    socket.emit('CLIENT_MSG', msg)
    setNewMessage(msg);
  }

  return (
    <div className="container">
      <div className="row">
        <div className="col-4">
          <div className="card">
            <div className="card-body">
              <div className="conrd-title">My first chat</div>
              <hr />
              <div className="messages">
                {messages.map(msg => {
                  return (
                    <div>{msg.username}: {msg.text}</div>
                  )
                })}
              </div>
            </div>
            <form onSubmit={e => sendMessage(e)}>
              <div className="card-footer">
                <input id="username" type="text" placeholder="Username" className="form-control"/>
                <br />
                <input id="text" type="text" placeholder="Your message" className="form-control"/>
                <br />
                <button type="submit" className="btn btn-primary form-control">Send</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  )
}

export default App;
